import React, { useContext, useState } from 'react';
import axios from 'axios';
import AppContext from './AppContext';
function PostItem(props) {
    const {dispatch} = useContext(AppContext);
    const [openEditForm,setOpenEditForm] = useState(false);
    const [OpenDeleteConfirm,setOpenDeleteConfirm] =useState(false)
    let date = new Date(props.data.createdAt);
    return (
        <div className="post-item">
        <p className="post-content">
           {props.data.content}
        </p>
        <div className="post-footer">
            <div className="post-infors">
                <span>{props.data.author.name}</span>
                <span>{`${date.getDate()}/${date.getMonth()+1}/${date.getFullYear()}`}</span>
            </div>
            {props.data.isEditable &&(
                <div className="post-edit-delete">
                    {OpenDeleteConfirm ? (
                        <>
                        <span className="delete-question">Are you sure?</span>
                        <span>Yes</span>
                        <span onClick={()=> setOpenDeleteConfirm(false)}>No</span>
                        </>
                    ):(
                        <>
                        <span onClick = {() =>setOpenEditForm(true)}>Edit</span>
                        <span onClick = {()=> setOpenDeleteConfirm(true)}>Delete</span>                      
                        </>
                    )}
            </div> 
            )}
        </div>
        {openEditForm&&(
            <>
            <div className="post-edit-form">
            <textarea
            type ="text"
            name ="content"
            id ="content"
            className="content"
            value ="Bộ phim này cũng đánh dấu sự tái hợp của cặp bài trùng Ưng Hoàng Phúc và Thu Thủy. Ngoài ra, cùng ngóng chờ những màn diễn xuất cực lôi cuốn từ các bộ sậu đình đám showbiz Việt Quách Ngọc Ngoan, Huỳnh Đông"
            />
            
            <div className="btn-container">
                <button className="btn" type ="button">Update</button>
                <button className="btn" type ="button" onClick={() =>setOpenEditForm(false)}>Cancel</button>
            </div>
            </div>
            </>
        )}
    </div>
    );
}

export default PostItem;
